mapper.select:
  	select menu_id menuId,permission_ permission
  	from sys_role_menu
	where role_id=#{roleId}

select CONCAT(menu_name,'(', GROUP_CONCAT(code_text), ')') from sys_role_menu rm join sys_menu m on rm.menu_id=m.id_ join sys_dic d on rm.permission_=d.code_ and d.type_='CRUD'
rm.role_id=#{roleId}
group by menu_name order by menu_id
