mapper.select:
  	select menu_id menuId,permission_ permission
  	from sys_user_menu
	where user_id=#{userId}

select CONCAT(menu_name,'(', GROUP_CONCAT(code_text), ')') from sys_user_menu rm join sys_menu m on rm.menu_id=m.id_ join sys_dic d on rm.permission_=d.code_ and d.type_='CRUD'
rm.user_id=#{userId}
group by menu_name order by menu_id
