mapper.select:
SELECT * 
FROM SYS_APP a
WHERE id = #{id}

mapper.update:
UPDATE SYS_APP a SET
	a.`name` = #{name},
	a.`code` = #{code},
	a.sort = #{sort},
	a.isEnable = #{isEnable}
WHERE a.`id` = #{id}

UPDATE SYS_APP a SET
	a.isEnable = #{isEnable}
WHERE 1 = 1

	AND a.`id` in #{item}

mapper.delete:
DELETE FROM SYS_APP
WHERE 1 = 1

	AND `id` in #{item}

mapper.select:
SELECT * FROM SYS_APP a WHERE 1 = 1 AND a.isEnable = #{isEnable}
AND EXISTS (SELECT 1 FROM SYS_RE_USER_APP ua WHERE ua.appId = a.id AND ua.userId = #{userId})
ORDER BY a.sort DESC, a.createTime DESC


     	SELECT a.code
		FROM SYS_APP a
