mapper.select:
SELECT * 
FROM SYS_ROLE a
WHERE id = #{id}

mapper.update:
UPDATE SYS_ROLE a SET
	a.isEnable = #{isEnable}
WHERE 1 = 1

	AND a.`id` in #{item}

mapper.select:
SELECT * FROM SYS_ROLE a WHERE 1 = 1
AND a.name like CONCAT('%',#{name},'%')
AND a.isEnable = #{isEnable}
AND a.appId = #{appId}
ORDER BY a.sort DESC

mapper.delete:
DELETE FROM SYS_ROLE
WHERE 1 = 1

	AND `id` in #{item}

DELETE FROM SYS_ROLE
WHERE 1 = 1

	AND appId in #{item}
